otPart");if  not humanoidRootPart then return;end if  not humanoidRootPart:FindFirstChild("FlyVelocity") then local bodyVel=Instance.new("BodyVelocity");bodyVel.Name="FlyVelocity";bodyVel.MaxForce=Vector3.new(100000,100000,100000);bodyVel.Velocity=Vector3.new(0,0,0);bodyVel.Parent=humanoidRootPart;end if self.Connections.FlyLoop then self.Connections.FlyLoop:Disconnect();end self.Connections.FlyLoop=RunService.RenderStepped:Connect(function() if  not self.Settings.FlyEnabled then return;end local hrp=player.Character and player.Character:FindFirstChild("HumanoidRootPart") ;local bodyVel=hrp and hrp:FindFirstChild("FlyVelocity") ;if (hrp and bodyVel) then local camera=workspace.CurrentCamera;local moveDirection=Vector3.new(0,0,0);if UserInputService:IsKeyDown(Enum.KeyCode.W) then moveDirection=moveDirection + camera.CFrame.LookVector ;end if UserInputService:IsKeyDown(Enum.KeyCode.S) then moveDirection=moveDirection-camera.CFrame.LookVector ;end if UserInputService:IsKeyDown(Enum.KeyCode.A) then moveDirection=moveDirection-camera.CFrame.RightVector ;end if UserInputService:IsKeyDown(Enum.KeyCode.D) then moveDirection=moveDirection + camera.CFrame.RightVector ;end if UserInputService:IsKeyDown(Enum.KeyCode.Space) then moveDirection=moveDirection + Vector3.new(0,1,0) ;end if UserInputService:IsKeyDown(Enum.KeyCode.LeftShift) then moveDirection=moveDirection-Vector3.new(0,1,0) ;end bodyVel.Velocity=moveDirection * self.Settings.FlySpeed ;end end);end;MovementManager.StopFlying=function(self) if self.Connections.FlyLoop then self.Connections.FlyLoop:Disconnect();self.Connections.FlyLoop=nil;end local player=Players.LocalPlayer;if (player and player.Character) then local hrp=player.Character:FindFirstChild("HumanoidRootPart");if hrp then local bodyVel=hrp:FindFirstChild("FlyVelocity");if bodyVel then bodyVel:Destroy();end end end end;MovementManager.ToggleInfiniteJump=function(self,enabled) self.Settings.InfiniteJump=enabled;if self.Connections.InfiniteJump then self.Conn