rue;end return false;end;HotkeyManager.SetToggleMode=function(self,enabled) self.ToggleMode=enabled;if  not enabled then self.Active=false;end end;HotkeyManager.Initialize=function(self) UserInputService.TextBoxFocused:Connect(function() self.IsTyping=true;end);UserInputService.TextBoxFocusReleased:Connect(function() self.IsTyping=false;end);end;local InputHandler={AimbotToggled=false};InputHandler.Initialize=function(self,aimbot,hotkey) self:CleanupOldConnections();UserInputService.InputBegan:Connect(function(input,gameProcessed) if hotkey.IsTyping then return;end if ((input.KeyCode==hotkey.Key) and hotkey.Enabled) then self:OnHotkeyPressed(aimbot,hotkey);end if ((input.KeyCode==CONFIG.AIMBOT_TOGGLE_KEY) and  not hotkey.IsTyping) then if (hotkey.Key~=CONFIG.AIMBOT_TOGGLE_KEY) then self:ToggleAimbot(aimbot);end end end);UserInputService.InputEnded:Connect(function(input,gameProcessed) if (input.KeyCode==hotkey.Key) then self:OnHotkeyReleased(aimbot,hotkey);end end);RunService.RenderStepped:Connect(function() self:UpdateAimbot(aimbot,hotkey);end);end;InputHandler.CleanupOldConnections=function(self) if  not getgenv().ExunysDeveloperAimbot then return;end local function cleanupSignal(signal) for _,connection in pairs(getconnections(signal)) do if connection.Function then Utils.SafeCall(function() local info=debug.getinfo(connection.Function);if (info and info.source and info.source:find("Exunys")) then connection:Disable();end end);end end end cleanupSignal(UserInputService.InputBegan);cleanupSignal(UserInputService.InputEnded);end;InputHandler.OnHotkeyPressed=function(self,aimbot,hotkey) if hotkey.ToggleMode then hotkey.Active= not hotkey.Active;if  not hotkey.Active then self:ClearLock();end else hotkey.Active=true;end end;InputHandler.OnHotkeyReleased=function(self,aimbot,hotkey) if  not hotkey.ToggleMode then hotkey.Active=false;if  not self.AimbotToggled then self:ClearLock();end end end;InputHandler.ToggleAimbot=function(self,aimbot) if  not aimbot.Loaded then r